from gmpy import mpz, invert, gcdext, gcd

def attack(n, e1, e2, c1, c2):
    rst = gcdext(e1, e2)
    s1 = rst[1]
    s2 = rst[2]

    if s1<0:
        s1 = -s1
        c1 = invert(c1, n)

    elif s2<0:
        s2 = -s2
        c2 = invert(c2, n)

    m = pow(c1, s1)*pow(c2, s2)
    return m
     
